* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;

}

#demo {
    word-break: break-all;
}

#div1 {
    position: fixed;
    left: 50%;
    transform: translateX(-50%);
    top: 20px;
}

#div1:after {
    content: '';
    display: block;
    position: absolute;
}

#div1:before {
    content: '';
    display: block;
    position: absolute;
}
@media (max-width:500px) {
    #Wrapper {
        height: 50vh;
    }

    #demo {
        height: 50vh;
        overflow: auto;
    }

    #div1 {
        position: relative;
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
    }
}
